mapper.select:
SELECT 
FROM 
WHERE subject_dataset_key = #{subjectDatasetKey}
  AND dataset_key = #{targetDatasetKey}
  AND subject_id = #{id}

SELECT 
FROM 
WHERE target_id = #{id}
  AND dataset_key = #{targetDatasetKey}

SELECT 
FROM 
WHERE subject_dataset_key = #{subjectDatasetKey}
  AND dataset_key = #{targetDatasetKey}
ORDER BY target_rank, key
