select.if:
SELECT count(*)
FROM name_usage_${datasetKey}
WHERE NOT is_synonym

mapper.select:
SELECT exists(
    SELECT id
    FROM name_usage_${datasetKey}
    WHERE NOT is_synonym
    LIMIT 1
)

SELECT count(*)
FROM name_usage_${datasetKey}
WHERE parent_id IS NULL AND NOT is_synonym

WITH RECURSIVE cl AS ( SELECT u.id, u.parent_id, n.scientific_name as name, n.authorship, n.rank FROM WHERE u.id = #{id}
UNION
SELECT u.id, u.parent_id, n.scientific_name, n.authorship, n.rank FROM , cl WHERE u.id = cl.parent_id ) SELECT * FROM cl WHERE cl.id != #{id}

SELECT id, dataset_sectors
FROM name_usage_${datasetKey}
WHERE id = #{id} AND NOT is_synonym

mapper.insert:
INSERT INTO name_usage_${datasetKey}
(id, , created, modified)
VALUES (#{id}, , now(), now())

mapper.update:
UPDATE name_usage_${datasetKey}
SET (, created, modified)
= (, now(), now())
WHERE id = #{id}

mapper.select:
SELECT 
FROM 
JOIN name_usage_3 p ON p.id=u.parent_id
WHERE p.sector_key=#{sectorKey}
AND u.sector_key!=#{sectorKey}

mapper.update:
UPDATE name_usage_${datasetKey}
SET dataset_sectors = to_jsonb( #{count, typeHandler=org.col.db.type.FastutilIntIntHandler}::jsonb )
WHERE id = #{id}
