mapper.select:
SELECT exists(
  SELECT id FROM 
  WHERE dataset_key=#{datasetKey}
  LIMIT 1
)

SELECT 
FROM 
WHERE dataset_key=#{key.datasetKey} AND n.id = #{key.id}

SELECT concat_ws(' ', scientific_name, authorship)
FROM 
WHERE dataset_key=#{datasetKey}

  AND sector_key = #{sectorKey}

ORDER BY scientific_name, authorship

mapper.update:
UPDATE name
SET (, created, modified) = (, now(), now())
WHERE dataset_key=#{datasetKey} AND id = #{id}

mapper.sql:
WHERE NOT EXISTS (
  SELECT NULL
  FROM name_usage u
  WHERE n.id = u.name_id AND n.dataset_key=u.dataset_key
)

  AND n.modified < #{before}

mapper.select:
