SELECT , nm.index_id AS namesIndexId, nm.type AS namesIndexType
FROM  LEFT JOIN name_match nm ON nm.name_id=n.id AND nm.dataset_key=#{datasetKey}
WHERE n.dataset_key = #{datasetKey}
ORDER BY rank, scientific_name

SELECT , nm.index_id AS namesIndexId, coalesce(nm.type,'NONE') AS namesIndexType
FROM  LEFT JOIN name_match nm ON nm.name_id=n.id AND nm.dataset_key=#{key.datasetKey}
WHERE n.dataset_key = #{key.datasetKey} AND n.id = #{key.id}
