mapper.select:
SELECT 
FROM name_rel nr
WHERE nr.dataset_key = #{key.datasetKey}
  AND nr.type=#{type}::NOMRELTYPE
  AND nr.name_id = #{key.id}
ORDER BY nr.id

SELECT 
FROM name_rel nr
WHERE nr.dataset_key = #{key.datasetKey}
  AND nr.type=#{type}::NOMRELTYPE
  AND nr.related_name_id = #{key.id}
ORDER BY nr.id

SELECT 
FROM 
WHERE dataset_key = #{key.datasetKey} AND nr.sector_key = #{key.id}
