mapper.update:
UPDATE name_usage
SET (parent_id, modified, modified_by) = (#{newParentId}, now(), #{userKey})
WHERE dataset_key = #{datasetKey} AND parent_id = #{parentId}  AND sector_key=#{sectorKey}

UPDATE name_usage
SET (parent_id, modified, modified_by) = (#{parentId}, now(), #{userKey})
WHERE dataset_key = #{key.datasetKey} AND id = #{key.id}

mapper.select:
SELECT 
FROM 
WHERE u.dataset_key = #{key.datasetKey} AND u.sector_key = #{key.id}

WITH RECURSIVE x AS( SELECT , concat_ws('|', n.scientific_name, u.id) AS path FROM WHERE u.dataset_key=#{datasetKey} AND UNION SELECT , concat_ws('|', x.path, not u.is_synonym, n.scientific_name, u.id) FROM JOIN x ON x.id = u.parent_id
u.dataset_key=#{datasetKey}
) SELECT id, scientific_name, authorship, rank, code, status, parent_id FROM x ORDER BY x.path

WITH RECURSIVE tree AS (
  SELECT id, name_id AS nid
  FROM name_usage
  WHERE dataset_key=#{key.datasetKey} AND id=#{key.id}
UNION
  SELECT c.id, c.name_id
  FROM name_usage c JOIN tree ON c.dataset_key=#{key.datasetKey} AND tree.id = c.parent_id
)
SELECT * FROM tree;
